def chat(data):

    if len(data)>2:
        if '?' in data:
            data=data.replace('?',' ')
            return questions(data)
        else:
            store(data)
            res = ['ok', 'fine', 'got it', 'point noted', 'thanks for letting me know',
                   'thats new information, I didnt know that']
            return choice(res)


